    private void btnKirimActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnKirimActionPerformed
        // TODO add your handling code here:
        try{
            String sql = "SELECT * FROM karyawan Where username='" + userText.getText() +
            "'AND password='" + passText.getText()+"'";
            java.sql.Connection conn = (Connection) config.getConnection();
            java.sql.PreparedStatement pst = conn.prepareStatement(sql);
            java.sql.ResultSet rs = pst.executeQuery(sql);

            boolean cekNim = userText.getText().equals(rs.getString("username"));
            boolean cekPass = passText.getText().equals((rs.getString("password")));

            if(rs.next()){
                if( cekNim && cekPass ){
                    JOptionPane.showMessageDialog(null, "Berhasil login");
                    this.setVisible(false);
                    new Main.Main().setVisible(true);
                }
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(this, "Username atau password salah");
        }
    }//GEN-LAST:event_btnKirimActionPerformed

