    private void userTextKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_userTextKeyPressed
        // TODO add your handling code here:
        if (evt.getKeyCode()==KeyEvent.VK_ENTER) {
            passText.requestFocus();
        }
    }//GEN-LAST:event_userTextKeyPressed

    private void passTextKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_passTextKeyPressed
        // TODO add your handling code here:
        if (evt.getKeyCode()==KeyEvent.VK_ENTER) {
            try{
                String sql = "SELECT * FROM karyawan Where username='" + userText.getText() +
                "'AND password='" + passText.getText()+"'";
                java.sql.Connection conn = (Connection)config.getConnection();
                java.sql.PreparedStatement pst = conn.prepareStatement(sql);
                java.sql.ResultSet rs = pst.executeQuery(sql);
                if(rs.next()){
                    if(userText.getText().equals(rs.getString("username"))
                        && passText.getText().equals((rs.getString("password")))){
                        JOptionPane.showMessageDialog(null, "Berhasil login");
                        this.setVisible(false);
                        new Main.Main().setVisible(true);;
                    }
                }else{
                    JOptionPane.showMessageDialog(null, "Username atau password salah");
                }
            }catch(Exception e){
                JOptionPane.showMessageDialog(this, e.getMessage());
            }
        }        
    }//GEN-LAST:event_passTextKeyPressed

